import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from 'src/app/service/auth.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent {

  signupForm: FormGroup | undefined;

  message:any;

  constructor(
    private service: AuthService,
    private fb: FormBuilder,private route: ActivatedRoute ,
    private router : Router
  ) { }

  ngOnInit() {
    this.signupForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', Validators.required, Validators.email],
      password: ['', Validators.required],
      confirmPassword: ['', Validators.required],
    }, { validator: this.passwordMatchValidator })
  }

  private passwordMatchValidator(fg: FormGroup) {
    const password = fg.get('password')?.value;
    const confirmPassword = fg.get('confirmPassword')?.value;
    if (password != confirmPassword) {
      fg.get("confirmPassword")?.setErrors({ passwordMismatch: true });
    } else {
      fg.get('confirmPassword')?.setErrors(null);
    }
  }

  signup() {
    console.log(this.signupForm.value);
    this.service.signup(this.signupForm.value).subscribe((response) => {
      this.succesMsg();
    })
  }
  succesMsg(){
    alert("*** User Registered Successfully ***")
    this.router.navigate(['login']);
  }

}


<div class="form-bg">

    <form [formGroup]="signupForm" class="form">
        <div class="box">
           
            <div style="display: flex; margin: 0 auto;">
                <h4 class="admin">SignUp</h4>
            </div>
            <div>
                <input type="text" name="name" id="name" placeholder="Enter your name" formControlName="name" required/>
            </div>
            <div>
                <input type="email" name="email" id="email" placeholder="Enter your email" formControlName="email" required/>
            </div>
            <div>
                <input type="password" name="password" id="password" placeholder="Enter your password" formControlName="password" required/>
            </div>
            <div>
                <input type="password" name="confirmPassword" id="confirmPassword" placeholder="Enter your confirm password" formControlName="confirmPassword" required/>
            </div>
            <div>
                <button class="SignUp" type="button" routerLink="/login">Already Registered ?</button>
            </div>

            <button class="login-button" type="button" [disabled]="signupForm.invalid" (click)="signup()">Signup</button>
        </div>
    </form>
</div>
